import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchRestaurantById } from '../api'; 
import './RestaurantDetail.css'; 

import restaurantImage from '../assets/detail-page-background.jpg'; 

const RestaurantDetail = () => {
  const { id } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadRestaurant = async () => {
      try {
        const data = await fetchRestaurantById(id);
        if (data) {
          setRestaurant(data);
        } else {
          setError('Restaurant not found.');
        }
      } catch (err) {
        setError('Failed to fetch restaurant details.');
      } finally {
        setLoading(false);
      }
    };

    loadRestaurant();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  if (!restaurant) return <div>Restaurant not found.</div>;

  return (
    <div className="restaurant-detail-container">
      <img 
        src={restaurantImage} 
        alt={restaurant['Restaurant Name']} 
        className="restaurant-image"
      />
      <div className="restaurant-info">
        <h1 className="restaurant-name">{restaurant['Restaurant Name']}</h1>
        <p className="cuisine">{restaurant['Cuisines']}</p>
        <p className="location">{restaurant['Locality']}, {restaurant['City']}</p>
        <p className="details">{restaurant['Address']}</p>
        <div className="rating-box" style={{ backgroundColor: restaurant['Rating color'] }}>
          Rating: {restaurant['Aggregate rating']}
        </div>
      </div>
      <div className="details-table">
        <div className="sub-details-cont">
          <div className="details-row">
            <span className="label">Votes:</span>
            <span className="value">{restaurant['Votes']}</span>
          </div>
          <div className="details-row">
            <span className="label">Price Range:</span>
            <span className="value">{restaurant['Price range']}</span>
          </div>
          <div className="details-row">
            <span className="label">Country Code:</span>
            <span className="value">{restaurant['Country Code']}</span>
          </div>
          <div className="details-row">
            <span className="label">Rating Text:</span>
            <span className="value">{restaurant['Rating text']}</span>
          </div>
          <div className="details-row">
            <span className="label">Average Cost for Two:</span>
            <span className="value">{restaurant['Average Cost for two']}</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RestaurantDetail;
