// src/components/RestaurantDetail.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { fetchRestaurantById } from '../api'; // Update path if needed

const RestaurantDetail = () => {
  const { id } = useParams();
  const [restaurant, setRestaurant] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadRestaurant = async () => {
      try {
        const data = await fetchRestaurantById(id);
        if (data) {
          setRestaurant(data);
        } else {
          setError('Restaurant not found.');
        }
      } catch (err) {
        setError('Failed to fetch restaurant details.');
      } finally {
        setLoading(false);
      }
    };

    loadRestaurant();
  }, [id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  if (!restaurant) return <div>Restaurant not found.</div>;

  return (
    <div>
      <h1>{restaurant['Restaurant Name']}</h1>
      <p>Address: {restaurant['Address']}</p>
      <p>Rating: {restaurant['Aggregate rating']}</p>
      <p>Average Cost for Two: {restaurant['Average Cost for two']}</p>
      <p>Cuisines: {restaurant['Cuisines']}</p>
      <p>City: {restaurant['City']}</p>
      <p>Country Code: {restaurant['Country Code']}</p>
      <p>Locality: {restaurant['Locality']}</p>
      <p>Price Range: {restaurant['Price range']}</p>
      <p>Rating Color: {restaurant['Rating color']}</p>
      <p>Rating Text: {restaurant['Rating text']}</p>
      <p>Votes: {restaurant['Votes']}</p>
    </div>
  );
};

export default RestaurantDetail;
