import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { fetchRestaurants } from '../api';

const RestaurantList = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalCount, setTotalCount] = useState(0);
  const [limit] = useState(20);
  const [country, setCountry] = useState('');
  const [avgCost, setAvgCost] = useState('');
  const [cuisines, setCuisines] = useState('');
  const [search, setSearch] = useState('');

  useEffect(() => {
    const loadRestaurants = async () => {
      try {
        setLoading(true);
        const { restaurants: fetchedRestaurants, totalCount, page: currentPage, totalPages: totalPagesCount } = await fetchRestaurants(page, limit, country, avgCost, cuisines, search);
        console.log('Fetched data:', fetchedRestaurants); // Debugging: Check the fetched data
        setRestaurants(fetchedRestaurants);
        setTotalCount(totalCount);
        setTotalPages(totalPagesCount);
        setPage(currentPage);
      } catch (err) {
        setError('Failed to fetch restaurants.');
      } finally {
        setLoading(false);
      }
    };

    loadRestaurants();
  }, [page, limit, country, avgCost, cuisines, search]);

  const handleSearch = () => {
    setPage(1); // Reset to first page when searching
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    if (name === 'search') setSearch(value);
    if (name === 'country') setCountry(value);
    if (name === 'avgCost') setAvgCost(value);
    if (name === 'cuisines') setCuisines(value);
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Restaurant List</h1>

      <div>
        <input
          type="text"
          name="search"
          placeholder="Search by name or description"
          value={search}
          onChange={handleFilterChange}
        />
        <input
          type="text"
          name="country"
          placeholder="Filter by country"
          value={country}
          onChange={handleFilterChange}
        />
        <input
          type="number"
          name="avgCost"
          placeholder="Max avg cost for two"
          value={avgCost}
          onChange={handleFilterChange}
        />
        <input
          type="text"
          name="cuisines"
          placeholder="Filter by cuisines"
          value={cuisines}
          onChange={handleFilterChange}
        />
        <button onClick={handleSearch}>Apply Filters</button>
      </div>

      {restaurants.length === 0 ? (
        <p>No restaurants available.</p>
      ) : (
        <ol>
          {restaurants.map((restaurant) => (
            <li key={restaurant['Restaurant ID']}>
              <Link to={`/restaurants/${restaurant['Restaurant ID']}`}>{restaurant['Restaurant Name']}</Link>
            </li>
          ))}
        </ol>
      )}

      <div>
        <button onClick={() => setPage((prevPage) => Math.max(prevPage - 1, 1))} disabled={page === 1}>
          Previous
        </button>
        <span> Page {page} of {totalPages} </span>
        <button onClick={() => setPage((prevPage) => Math.min(prevPage + 1, totalPages))} disabled={page === totalPages}>
          Next
        </button>
      </div>
    </div>
  );
};

export default RestaurantList;
