// src/components/RestaurantList.js
import React, { useEffect, useState } from 'react';
import { fetchRestaurants } from '../api';
import { Link } from 'react-router-dom';

const RestaurantList = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    const loadRestaurants = async () => {
      const data = await fetchRestaurants(currentPage);
      if (data && Array.isArray(data.restaurants)) {
        setRestaurants(data.restaurants);  // Adjust based on your API response
        setTotalPages(data.totalPages);    // Adjust based on your API response
      }
    };
    loadRestaurants();
  }, [currentPage]);

  return (
    <div>
      <h1>Restaurant List</h1>
      <ul>
        {restaurants.length > 0 ? (
          restaurants.map(restaurant => (
            <li key={restaurant.id}>
              <Link to={`/restaurants/${restaurant.id}`}>{restaurant.name}</Link>
            </li>
          ))
        ) : (
          <p>No restaurants available.</p>
        )}
      </ul>
      <div>
        <button onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}>Previous</button>
        <span> Page {currentPage} of {totalPages} </span>
        <button onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}>Next</button>
      </div>
    </div>
  );
};

export default RestaurantList;
