import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom'; // Import Link for navigation
import { fetchRestaurants } from '../api'; // Import the fetchRestaurants function

const RestaurantList = () => {
  const [restaurants, setRestaurants] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [totalCount, setTotalCount] = useState(0);
  const [limit] = useState(20); // Display 20 items per page

  useEffect(() => {
    const loadRestaurants = async () => {
      try {
        const { restaurants: fetchedRestaurants, totalCount, page: currentPage, totalPages: totalPagesCount } = await fetchRestaurants(page, limit); // Fetch restaurants data
        setRestaurants(fetchedRestaurants);
        setTotalCount(totalCount);
        setTotalPages(totalPagesCount);
        setPage(currentPage);
      } catch (err) {
        setError('Failed to fetch restaurants.');
      } finally {
        setLoading(false);
      }
    };

    loadRestaurants();
  }, [page, limit]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h1>Restaurant List</h1>
      {restaurants.length === 0 ? (
        <p>No restaurants available.</p>
      ) : (
        <ol>
          {restaurants.map((restaurant) => (
            <li key={restaurant.id}>
              <Link to={`/restaurants/${restaurant.id}`}>{restaurant.name}</Link>
            </li>
          ))}
        </ol>
      )}
      <div>
        <button onClick={() => setPage((prevPage) => Math.max(prevPage - 1, 1))} disabled={page === 1}>
          Previous
        </button>
        <span> Page {page} of {totalPages} </span>
        <button onClick={() => setPage((prevPage) => Math.min(prevPage + 1, totalPages))} disabled={page === totalPages}>
          Next
        </button>
      </div>
    </div>
  );
};

export default RestaurantList;
