// src/api.js
const API_BASE_URL = 'http://localhost:5000'; // Ensure this matches your Flask API URL

export const fetchRestaurants = async (page = 1, limit = 10) => {
  try {
    const response = await fetch(`${API_BASE_URL}/restaurants?page=${page}&limit=${limit}`);
    if (!response.ok) {
      throw new Error('Network response was not ok');
    }
    return await response.json();
  } catch (error) {
    console.error('Error fetching restaurants:', error);
    return []; // Return an empty array if there's an error
  }
};

export const fetchRestaurantById = async (id) => {
  try {
    const response = await fetch(`${API_BASE_URL}/restaurants/${id}`);
    if (!response.ok) {
      throw new Error('Network response was not ok');
    }
    return await response.json();
  } catch (error) {
    console.error('Error fetching restaurant details:', error);
    return null; // Return null if there's an error
  }
};
